$(document).ready(function() {
  ScrollToTop(".tab"),
    ScrollTransitions("index-banner", "menu", "#menu"),
    adhoc_parallax("#index-banner", "0.5"),
    $("#Album__Navbar__BackArrow").click(function() {
      $("#Gallery__Grid").fadeOut(0),
        $("#Album__Navbar").fadeOut(0),
        $("#Albums__Grid").fadeIn(400),
        $("#index-banner").fadeIn(400),
        $("#menu").fadeIn(400),
        $("ul.tabs").tabs("select_tab", "test1");
    }),
    document.documentElement.clientWidth < 768 &&
      TextSlice("#FooterText", 30, ""),
    $.ajax({
      url: "https://api.vk.com/method/photos.getAlbums?",
      data: {
        owner_id: "-72629433",
        need_covers: "1",
        photo_sizes: "1",
        access_token:
          "9fba3e7f9fba3e7f9fba3e7fc19fe6753799fba9fba3e7fc692698db38757a9dfe6caa0",
        v: "5.95"
      },
      dataType: "jsonp",
      success: function(a) {
        a.response.items.forEach(function(a) {
          for (i = 0; i < 9; i++) {
            if ("r" == a.sizes[i].type) break;
          }
          var e = (a.id, a.sizes[i].src),
            s = a.title;
          $(
            '<div class="col s12 m6 l4"><div class="card waves-effect waves-block waves-light z-depth-5"><div class="card-image"><a class="ajax" onclick="albums(this)" href="#AdHoc__app"><img class="padding" src="' +
              e +
              '"/><div class="aid">' +
              a.id +
              '</div><span class="card-title">' +
              s +
              "</span></a></div></div></div></div>"
          ).appendTo(".nav-albs");
        });
      }
    }),
    $("#year").html(new Date().getFullYear());
});
